import { useState } from "react";
import "./App.css";
import Component1 from "./component/comp1";

function App() {
  const [name, setName] = useState("Nikita");
  const [age, setAge] = useState("20");

  // let age = 20;
  console.log("name is", name);

  const handleOnClick = () => {
    setName("Neha");
    // console.log("age is", age);
    setAge = "40";
    // console.log("updated age is", age);
  };

  return (
    <div className="App">
      <h1>Context Api</h1>
      <button onClick={handleOnClick}>change Name</button>
      <p>
        {name}
        {age}
      </p>
      <Component1 nameprop={name} />
    </div>
  );
}

export default App;
