import { useState } from "react";
import "./App.css";
import Component1 from "./component/comp1";

function App() {
  const [name, setName] = useState("Nikita");
  const [age, setAge] = useState(10);

  // let age = 20;
  console.log("name is", name);

  const handleOnClickName = () => {
    setName("Neha");
  };

  const handleOnClickAge = () => {
    setAge(20);
  };

  return (
    <div className="App">
      <h1>Context Api</h1>
      <button onClick={handleOnClickName}>change Name</button>
      <button onClick={handleOnClickAge}>change Age</button>

      <p>
        {name}
        {age}
      </p>
      <Component1 nameprop={name} ageprop={age} />
    </div>
  );
}

export default App;
